import Image from "./Image"
import Button from "./button"

export default function EmailTemplate({ data }) {
  // Check if emailContent is defined and is a string
  console.log(data, "email data");

  if (!data || typeof data.emailContent !== 'string') {
    return <div className="text-red-500 text-center">Invalid email content.</div>;
  }

  // Use the email content directly
  const emailContent = data.emailContent;

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-lg max-w-2xl mx-auto">
      <div className="bg-white p-8 rounded-lg" dangerouslySetInnerHTML={{ __html: emailContent }} />
    </div>
  )
}
