import Image from "./Image"
import Button from "./button"

export default function EmailTemplate({ data }) {
  if (!data) {
    return <div className="bg-white p-6 rounded-lg shadow-md text-center">No data available</div>;
  }

  const emailContent = data.emailContent; // Assuming data contains the emailContent as a string

  return (
    <div className="bg-white p-6 rounded-lg shadow-md" dangerouslySetInnerHTML={{ __html: emailContent }} />
  );
}
