import React, { useState } from "react";
import axios from "axios";

const GenerateEmailContent = () => {
  const [formData, setFormData] = useState({
    brand: "Deep Blue Health",
    segment: "Past buyers of joint health products and site visitors who viewed joint-related pages",
    goal: "Promote best-selling joint health supplement to convert interested customers",
    tone: "Professional, persuasive, and informative",
  });

  const [emailContent, setEmailContent] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const generateEmail = async () => {
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:8000/api/generate-email-content", formData);
      setEmailContent(response.data.emailContent);
    } catch (error) {
      console.error("Error:", error);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>AI-Generated Email Builder</h1>
      <div className="form">
        <label>Brand Name:</label>
        <input type="text" name="brand" value={formData.brand} onChange={handleChange} />

        <label>Target Segment:</label>
        <input type="text" name="segment" value={formData.segment} onChange={handleChange} />

        <label>Campaign Goal:</label>
        <input type="text" name="goal" value={formData.goal} onChange={handleChange} />

        <label>Email Tone:</label>
        <input type="text" name="tone" value={formData.tone} onChange={handleChange} />

        <button onClick={generateEmail} disabled={loading}>
          {loading ? "Generating..." : "Generate Email"}
        </button>
      </div>

      {emailContent && (
        <div className="email-preview">
          <h2>Email Preview</h2>
          <div dangerouslySetInnerHTML={{ __html: emailContent }} />
        </div>
      )}
    </div>
  );
};

export default GenerateEmailContent;
