import Image from "./Image"
import Button from "./button"

export default function EmailTemplate({ data }) {
  if (!data) {
    return <div className="bg-white p-6 rounded-lg shadow-md text-center">No data available</div>;
  }

  console.log(data,"email data")

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <header className="text-center mb-8">
          <Image
            src={data.logoUrl || "/placeholder.svg"}
            alt={data.brandName || "Brand Logo"}
            width={200}
            height={50}
            className="mx-auto mb-4"
          />
          <h1 className="text-3xl font-bold text-blue-800">{data.brandName || "Brand Name"}</h1>
        </header>

        {/* Main Content */}
        <main>
          <h2 className="text-2xl font-semibold mb-4 text-blue-700">Discover the Power of {data.productName || "Product"}</h2>
          <p className="mb-6 text-gray-700">{data.productDescription || "Product description goes here."}</p>

          {/* Product Showcase */}
          <div className="mb-8">
            <Image src={data.productImageUrl || "/placeholder.svg"} alt={data.productName || "Product Image"} width={400} height={300} className="rounded-lg mb-4" />
            <h3 className="text-xl font-semibold mb-2 text-blue-600">{data.productName || "Product Name"}</h3>
            <p className="text-gray-600">
              {data.productShowcaseDescription || "Experience natural joint health support from New Zealand's pristine waters."}
            </p>
          </div>

          {/* CTA Button */}
          <div className="text-center mb-8">
            <Button className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-full text-lg">
              {data.ctaText || "Shop Now"}
            </Button>
          </div>

          {/* Testimonial */}
          <div className="bg-gray-100 p-6 rounded-lg mb-8">
            <h3 className="text-xl font-semibold mb-2 text-blue-700">What Our Customers Say</h3>
            <p className="italic text-gray-600">&quot;{data.testimonial || "No testimonials available."}&quot;</p>
          </div>
        </main>

        {/* Footer */}
        <footer className="text-center text-gray-500 text-sm">
          <p>&copy; {new Date().getFullYear()} {data.brandName || "Brand Name"}. All rights reserved.</p>
        </footer>
      </div>
    </div>
  )
}
